--
-- Cấu trúc bảng cho bảng `truyen`
--

CREATE TABLE `truyen` (
  `id` int(10) UNSIGNED NOT NULL,
  `TieuDe` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `TomTat` text COLLATE utf8_unicode_ci NOT NULL,
  `TacGia` longtext COLLATE utf8_unicode_ci NOT NULL,
  `Hinh` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `NoiBat` int(11) NOT NULL DEFAULT 0,
  `SoLuotXem` int(11) NOT NULL DEFAULT 0,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `truyen`
--

INSERT INTO `truyen` (`id`, `TieuDe`, `TomTat`, `TacGia`, `Hinh`, `NoiBat`, `SoLuotXem`, `created_at`, `updated_at`) VALUES
(1016, 'Kenja no Deshi wo Nanoru Kenja', 'Sakimori Kagami role-play một nhân vật tên 「Dunbalf」 vốn là một triệu hồi sư được trọng vọng và cũng là một trong Cửu Đại Hiền Nhân trong VRMMO-RPG 【Ark-Ars Online】. Trong lúc đang chơi cậu vô tình ngủ quên và khi tỉnh dậy phát hiện ra thế giới ảo đã hóa thành hiện thực. Tuy nhiên, vị hiền nhân đã không còn là vị hiền nhân như lúc trước', 'Fuji Choco / 藤ちょこ', 'SBgC_yande.re-304829-sample-dress-elf-fuzichoko-landscape-pointy_ears-sword-thighhighs.jpg', 1, 123, '2020-07-14 11:46:44', '2020-07-14 23:27:01'),
(1017, 'No Game No Life', 'Trong thế giới trò chơi, có một truyền thuyết đô thị về 『　　』 người đã trở thành người chơi giỏi nhất và chiến thắng từng game một trên thế giới. Không ai biết 『　　』 trông như thế nào cũng như không biết về danh tính của anh. Trên thực tế 『　　』 là một đội được thành lập bởi hai người, người mà đều là Hikikomori, NEET, nằm ngoài xã hội, và là otaku, cụ thể là Sora và Shiro.', 'Kamiya Yuu (榎宫祐)', 'q6RQ_512323.jpg', 0, 191, '2020-07-14 12:29:07', '2020-07-15 00:05:40'),
(1018, 'Master Hunter K', '“Bằng chính sức mạnh của ngươi, hãy chứng minh rằng giống loài của ngươi xứng đáng đi”Không một lời cảnh báo, từng người đàn ông, phụ nữ, và cả trẻ nhỏ đều bị dịch chuyển tới chiến đấu trong chuỗi những trận chiến dài vô tận. Nếu không thể hoàn thành mọi mục tiêu thì nhân loại sẽ bị thanh trừng, hay đó là những gì mà Người thừa hành nói.Nhưng nếu đúng là vậy, thì tại sao K lại được gửi trở về quá khứ?', 'From Hell (프롬헬)', '8ZOu_55766168_p0.jpg', 0, 245, '2020-07-14 12:45:30', '2020-07-14 22:53:02'),
(1019, 'Genjitsushugi Yuusha no Oukokusaikenki', 'Souma Kazuya được triệu&nbsp;hồi bằng&nbsp;nghi thức kêu gọi Anh hùng đến một thế giới mà Quỷ Vương đã chiếm được 1/5 lãnh thổ. Nhưng rồi Vị vua bỗng có hứng thú với kế hoạch xây dựng nước giàu quân mạnh của anh và bằng cách nào đó quăng anh lên Ngai vàng. Soma, người bị ép phải kế vị bỏ luôn con đường diệt Quỷ và cùng với những nhân tài mà anh tập hợp được, đi theo con đường xây dựng đất nước với đầu óc&nbsp;thực tế.', 'Dozeumaru (どぜう丸)', 'l7M8_fffe5000a8880033c912f8bd91894519-1-727x1024.jpg', 1, 2, '2020-07-14 15:52:10', '2020-07-14 22:53:38'),
(1020, 'Tsuki ga Michibiku Isekai Douchuu', 'Một ngày nọ học sinh trung học bình thường Misumi Makoto bỗng dưng&nbsp;được Tsukuyomi no Mikoto – vị thần của màn đêm và mặt trăng yêu cầu sang thế giới khác, trở thành anh hùng&nbsp;cứu giúp cư dân nơi đó. Tuy nhiên, sau khi được triệu hồi cậu lại bị coi như đồ bỏ và bị ném đến nơi tận cùng thế giới. Như Tsukuyomi-sama đã từng nói cậu phải chọn lấy con đường cho bản thân mình, giờ đây Misumi Makoto phải&nbsp;tự mình đi tìm con đường riêng.', 'Azumi Kei', 'GuRr_works_moon6.jpg', 0, 0, '2020-07-14 15:57:34', '2020-07-14 15:59:15'),
(1021, 'Solo Leveling', 'Cách đây 10 năm, sau khi những “Cánh cổng” kết nối thế giới thực với thế giới quái vật mở ra, một số người bình thường, hàng ngày đã nhận được sức mạnh để săn quái vật trong những Cánh cổng.Họ được biết đến với tên là Thợ săn.Tuy nhiên, không phải tất cả các Thợ săn đều mạnh mẽ.Tên tôi là Sung Jinwoo, một Thợ săn hạng E.Tôi là một người phải mạo hiểm mạng sống của mình trong những hầm ngục thấp nhất, “một thợ săn yếu nhất Thế giới”.', 'Chugong,Jang Sung-Lak,추공', '9NU7_1-691x1024.jpg', 0, 0, '2020-07-14 16:04:06', '2020-07-14 22:55:06'),
(1022, 'Hello, Hello and Hello', '“Nè, Yoshi-kun. Tớ thích…”\r\nNhững lời nói đó là thứ đã ngay lập tức khiến tôi chú ý. Trên đường đi học về. Trên sân thể thao của trường cấp ba, hay trong tiệm sách trước ga. Và ngay tại nơi chú mèo trắng đang say giấc. Yuki Shiina, một cô gái tôi không quen biết nhưng không hiểu tại sao lại biết tất cả về tôi, luôn tìm cách bắt chuyện với tôi như thế.', 'Haruki Aya (葉月文)', 'Cezn_main_cover.jpg', 1, 0, '2020-07-14 16:13:04', '2020-07-14 22:56:04'),
(1023, 'Touhou no Maou', 'Tại một thế giới nơi mà con người sống cùng các chủng tộc khác. Sự tồn tại của phép màu tại nơi đây không có gì là kì lạ, hơn thế nữa, loài người đã kết hợp sức mạnh của ma thuật và khoa học. Nhờ đó mà thế giới đạt thêm một bước tiến mới. Tuy nhiên, vẫn luôn có những kẻ có dã tâm sử dụng thứ sức mạnh đó cho mục đích xấu xa của mình, chúng thâu tóm sức mạnh của cả hai thứ. Trở nên mạnh mẽ hơn những pháp sư còn lại.', 'Đỗ Phan Minh Ngọc', 'AMXJ_122032241-288-k836745.jpg', 1, 0, '2020-07-14 16:18:41', '2020-07-14 22:56:56'),
(1024, 'Emperor of Solo Play', 'Năm 2035. Trò chơi thực tế ảo Warlord đã thay đổi thế giới. An Jaehyun là một trong số những người ao ước muốn thay đổi cuộc sống thực bằng thế giới ảo. Sau khi cống hiến cuộc sống của anh ta để chơi game, anh ta đã bị phản bội. Bởi chính người bằng hữu của anh ta. Kết cục là JaeHyun mất hết mọi thứ. Nhưng mà game thủ ấy đã chợp lấy được một cơ hội, một cơ hội để làm lại tất cả! “Ta sẽ không chơi với ai bao giờ nữa hết. Dù kết quả có là gì, ta sẽ chứng minh rằng ta có thể làm được một mình.”', 'D-Dart ( 디다트 )', 'AQE6_c5d2359bbe9125a86126743f7a62b0e0.jpg', 1, 0, '2020-07-14 16:23:53', '2020-07-14 22:57:19'),
(1025, 'Legend', 'Reiji Saeki, sống ở một thị trấn nông thôn phía đông bắc, đã mất mạng trong một vụ tai nạn xảy ra ở giữa kỳ nghỉ hè……. Thế nhưng, khi cậu biết được điều đó, cậu đã thấy mình ở trong một khoảng không trắng xóa và đâu ra một quả cầu ánh sáng bí ẩn lượn lờ ở trước mắt. Quả cầu ánh sáng là một pháp sư đến từ một thế giới song song với trái đất, ông ta đang tìm kiếm một người đủ tư cách để thừa hưởng ma thuật họ đã tạo ra nhằm ngăn chặn nó biến mất.', 'Kannazuki Kurenai', 'Q2vG_71D5kvZBT0L.jpg', 0, 0, '2020-07-14 16:34:40', '2020-07-14 22:57:43'),
(1026, 'Tower of Karma', 'Al là một nô lệ, con trai của một ả gái mại dâm. Bị hành hạ và sỉ nhục liên miên, cuộc sống của cậu thật khốn khổ nhưng một khi vẫn còn có người chị gái của mình, thành viên còn lại duy nhất còn lại trong gia đình, chung sống với nhau, thì cậu cảm thấy như mình có thể đứng vững được trước mọi sóng gió của cuộc đời. Nhưng rồi một ngày kia, người chị gái của Al bán thân cho một quý tộc để lấy tiền giải phóng cho cậu khỏi kiếp nô lệ.', '富士田けやき', 'Lfl4_unnamed.png', 1, 0, '2020-07-14 16:42:46', '2020-07-14 22:58:14'),
(1027, 'Tensei Shitara Slime Datta Ken', 'Một người đàn ông tốt tính, 37 tuổi khi đang đi trên đường thì bị một tên cầm dao đâm chết. Khi ý thức quay trở lại, người đàn ông mới phát hiện ra mình đã biến thành một con Slime!', 'Fuse (伏瀬)', 'dZW2_slime_softcover_1.jpg', 1, 201, '2020-07-14 16:47:04', '2020-07-14 22:59:03'),
(1028, 'Dragon Bloodline', 'Một võ sư (42 tuổi, độc thân) dùng tất cả tâm huyết và luyện tập kỹ năng sử dụng kiếm và võ thuật của mình, nhưng sau đó bị cuốn vào một thảm họa lớn, và đã cứu được rất nhiều người trước khi trút hơi thở cuối cùng. Ai có thể nghĩ rằng ông ta lại được đưa đến một thế giới khác sau khi chết, và một người phụ nữ xinh đẹp tự xưng là Tổng Quản xuất hiện? Ông ta được bảo rằng ông có thể hồi sinh ở một thế giới với kiếm và ma thuật, và ông nhận được một Gift đầy uy lực mang tên [Dragon’s Bloodline]', 'God of D. [ Inactive ]', 'vvJ4_unnamed (1).png', 1, 0, '2020-07-14 16:54:03', '2020-07-14 22:59:38'),
(1030, 'Kimi no na ha', '“Không hiểu vì lí do gì, tôi nhận ra mình đang luôn đi tìm một ai đó”</p>\r\n\r\n<p>“Đó là thứ tuyệt đối không thể quên. Đó là tên của người mà mình yêu thương nhất. Tuyệt đối không thể quên.”', 'Makoto Shinkai', 'vbiS_img_1_m.jpg', 0, 0, '2020-07-14 17:05:30', '2020-07-14 23:00:33'),
(1031, 'Overlord', 'Câu chuyện bắt đầu trong những giây phút cuối tại tại Yggdrasil, một game online đình đám sắp phải đóng cửa. Nhân vật chính Momonga của chúng ta quyết định ở lại đến tận những phút cuối cùng với trò chơi yêu thích của mình và chờ server down. Bất ngờ, server không shutdown và Momonga bị mắc kẹt trong nhân vật của chính mình và “xuyên việt” tới thế giới khác.', 'Maruyama Kugane', 'YY6x_300px-Overlord_poster.jpg', 1, 0, '2020-07-14 17:07:03', '2020-07-14 23:00:54'),
(1032, 'Re:Monster', 'Tomokui Kanata được đầu thai thành chủng loài yếu nhất, Goblin, và nhận được một cái tên mới – Rou sau khi trải qua một cái chết kém may mắn. Tuy nhiên, Goblin Rou vẫn còn giữ được ký ức của kiếp trước,có được sự tiến hóa bất thường, cũng như trở nên đủ mạnh để tăng chỉ số bằng cách ăn, và như thế Rou tự hứa với bản thân sẽ sống thật trọn vẹn.', 'Kanekiru Kogitsune', 'NFOH_coverImage_2217284.jpg', 1, 160, '2020-07-14 17:08:27', '2020-07-14 23:01:12'),
(1033, 'Yandere', 'Chỉ đơn giản là một vài mẩu truyện bổ phổi về Yandere girls ', 'Ice', 'T1l6_bb70f89b76c43f28450e26d7fb3199f33b38226d_00.jpg', 1, 0, '2020-07-14 17:15:39', '2020-07-14 23:01:40'),
(1034, 'Tate no Yuusha no Nariagari', 'Về bản Light Novel này thì đa số các bác dịch là người mới nên còn nhiều yếu kém . Và bọn mình cho các bác ấy tự tập solo từ từ cho quen dần và bác Kai chỉ hỗ trợ 1 tý chứ không edit hoàn toàn . Nên bài dịch có sai sót , khá thô thì các bác cứ góp ý thẳng thắn để các bác ấy cải thiện hơn .&nbsp;~Neko~', 'Aneko Yusagi', 'KhuW_Cover_5.jpg', 1, 0, '2020-07-14 17:22:29', '2020-07-14 23:01:26'),
(1035, 'dargon ball z', '<p>jdjafaf</p>', 'fafafa', 'lVdE_Cover_5.jpg', 0, 0, '2020-07-16 02:00:35', '2020-07-16 02:00:35');

